import React, {useState, useEffect} from 'react';
import axios from "./axios";
import "./Row.css";

const base_URL = "https://image.tmdb.org/t/p/original/";

function Row({title, fetchURL}) {

     const [movies, setMovies] = useState([]);

    //  a snippet off code which runs based on a apecific condition
useEffect(() => {
//if [blank], run once wheen the row loads, and dont run again 
async function fetchData(){
    const request = await axios.get(fetchURL);
    //axios.get(fetchURL) does => https://api.themoviedb.org/3/discover/tv?api_key=${API_KEY}&with_networks=213
    console.log(request);
    setMovies(request.data.results);
    return request;
}
fetchData();
}, [fetchURL]);

console.log(movies);

    return (
        <div className="row">
            {/* //title */}
            <h2>{title}</h2>

    
            {/* container => posters */}

            <div className="row_posters">

                {movies.map(movie => (
                <img 
                key={movie.id}
                className="row_poster" 
                    src={`${base_URL}${movie.poster_path}`} alt={movie.name}/>
                ))}

            </div>
            
        </div>
    )
}

export default Row
